import React, { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from './ui/card';
import { ArrowUpCircle, ArrowDownCircle, Info } from 'lucide-react';
import { Prediction } from '../types';

interface Props {
  prediction: Prediction;
}

const formatPrice = (price: number) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(price);
};

const formatUsdc = (amount: number) => {
  return `${amount.toFixed(2)} USDC`;
};

const PredictionCard: React.FC<Props> = ({ prediction }) => {
  const [amount, setAmount] = useState("");
  const [selectedOutcome, setSelectedOutcome] = useState<'yes' | 'no' | null>(null);

  const calculatePayout = (outcome: 'yes' | 'no', amount: number) => {
    const price = outcome === 'yes' ? prediction.yesPrice : prediction.noPrice;
    const shares = amount / price;
    return shares;  // Each share pays out 1 USDC if correct
  };

  const priceChange = prediction.predictedPrice - prediction.currentPrice;
  const priceChangePercent = (priceChange / prediction.currentPrice) * 100;

  return (
    <Card>
      <CardHeader>
        <CardTitle>Will {prediction.asset} reach {formatPrice(prediction.predictedPrice)}?</CardTitle>
      </CardHeader>

      <CardContent>
        {/* Price Information */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div>
            <p className="text-sm text-gray-600">Current Price</p>
            <p className="text-xl font-bold">{formatPrice(prediction.currentPrice)}</p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Target Price (24h)</p>
            <p className={`text-xl font-bold ${priceChange >= 0 ? 'text-green-600' : 'text-red-600'}`}>
              {formatPrice(prediction.predictedPrice)}
              <span className="text-sm ml-2">
                ({priceChangePercent > 0 ? '+' : ''}{priceChangePercent.toFixed(2)}%)
              </span>
            </p>
          </div>
        </div>

        {/* Market Stats */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div>
            <p className="text-sm text-gray-600">Trading Volume</p>
            <p className="font-medium">{formatUsdc(prediction.volume)}</p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Liquidity</p>
            <p className="font-medium">{formatUsdc(prediction.liquidity)}</p>
          </div>
        </div>

        {/* Bidding Options */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <button
            onClick={() => setSelectedOutcome('yes')}
            className={`p-4 rounded-lg border ${
              selectedOutcome === 'yes' 
                ? 'border-green-500 bg-green-50' 
                : 'border-gray-200 hover:border-green-200'
            }`}
          >
            <div className="flex items-center gap-2 mb-2">
              <ArrowUpCircle className="text-green-500" />
              <span className="font-medium">Bid YES</span>
            </div>
            <p className="text-2xl font-bold">${prediction.yesPrice.toFixed(3)}</p>
            <p className="text-sm text-gray-600">Price per share</p>
          </button>

          <button
            onClick={() => setSelectedOutcome('no')}
            className={`p-4 rounded-lg border ${
              selectedOutcome === 'no' 
                ? 'border-red-500 bg-red-50' 
                : 'border-gray-200 hover:border-red-200'
            }`}
          >
            <div className="flex items-center gap-2 mb-2">
              <ArrowDownCircle className="text-red-500" />
              <span className="font-medium">Bid NO</span>
            </div>
            <p className="text-2xl font-bold">${prediction.noPrice.toFixed(3)}</p>
            <p className="text-sm text-gray-600">Price per share</p>
          </button>
        </div>

        {/* Trading Interface */}
        {selectedOutcome && (
          <div className="border rounded-lg p-4">
            <h3 className="font-semibold mb-4">
              Place {selectedOutcome.toUpperCase()} Bid
            </h3>

            <div className="space-y-4">
              <div>
                <label className="text-sm text-gray-600">Amount (USDC)</label>
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="w-full border rounded-lg p-2 mt-1"
                  placeholder="Enter amount in USDC"
                />
              </div>

              {amount && (
                <div className="bg-gray-50 p-3 rounded-lg">
                  <div className="flex justify-between text-sm mb-2">
                    <span>Share Price:</span>
                    <span>${selectedOutcome === 'yes' ? prediction.yesPrice : prediction.noPrice}</span>
                  </div>
                  <div className="flex justify-between text-sm mb-2">
                    <span>Shares to Receive:</span>
                    <span>{calculatePayout(selectedOutcome, Number(amount)).toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between font-medium">
                    <span>Max Payout:</span>
                    <span>{formatUsdc(calculatePayout(selectedOutcome, Number(amount)))}</span>
                  </div>
                </div>
              )}

              <button
                className={`w-full py-3 rounded-lg font-medium text-white ${
                  selectedOutcome === 'yes' ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'
                } disabled:bg-gray-300`}
                disabled={!amount || Number(amount) <= 0}
              >
                Place Bid
              </button>
            </div>
          </div>
        )}

        {/* Analysis */}
        <div className="mt-6">
          <h3 className="font-semibold mb-2">Analysis</h3>
          <p className="text-gray-700">{prediction.reasoning}</p>
        </div>
      </CardContent>
    </Card>
  );
};

export default PredictionCard;
