import React, { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { ArrowUpCircle, ArrowDownCircle, Info } from 'lucide-react';

interface MarketData {
  volume_24h: number;
  market_cap: number;
  percent_change_24h: number;
  percent_change_7d: number;
}

interface Prediction {
  asset: string;
  currentPrice: number;
  predictedPrice: number;
  confidence: number;
  reasoning: string;
  marketData?: MarketData;
  yesPrice: number;
  noPrice: number;
  volume: number;
  liquidity: number;
}

interface Props {
  prediction: Prediction;
  isAI: boolean;
}

const formatPrice = (price: number) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(price);
};

const formatUsdc = (amount: number) => {
  return `${amount.toFixed(2)} USDC`;
};

const PredictionCard: React.FC<Props> = ({ prediction, isAI }) => {
  const [amount, setAmount] = useState("");
  const [bidType, setBidType] = useState<'bid' | 'bidAgainst' | null>(null);

  const calculatePayout = (amount: number, price: number) => {
    return amount / price;  // Each share pays out 1 USDC if correct
  };

  const priceChange = prediction.predictedPrice - prediction.currentPrice;
  const priceChangePercent = (priceChange / prediction.currentPrice) * 100;

  return (
    <Card className="w-full max-w-4xl">
      <CardHeader>
        <div className="flex justify-between items-center">
          <CardTitle>Will {prediction.asset} reach {formatPrice(prediction.predictedPrice)}?</CardTitle>
          <span className="text-sm text-gray-500">
            {isAI ? 'AI Prediction' : 'KOL Prediction'}
          </span>
        </div>
      </CardHeader>

      <CardContent>
        {/* Current Market Status */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div>
            <p className="text-sm text-gray-600">Current Price</p>
            <p className="text-xl font-bold">{formatPrice(prediction.currentPrice)}</p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Target Price (24h)</p>
            <p className={`text-xl font-bold ${priceChange >= 0 ? 'text-green-600' : 'text-red-600'}`}>
              {formatPrice(prediction.predictedPrice)}
              <span className="text-sm ml-2">
                ({priceChangePercent > 0 ? '+' : ''}{priceChangePercent.toFixed(2)}%)
              </span>
            </p>
          </div>
        </div>

        {/* Bidding Options */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div 
            className={`p-6 rounded-lg border cursor-pointer transition-all ${
              bidType === 'bid' 
                ? 'border-green-500 bg-green-50' 
                : 'border-gray-200 hover:border-green-200'
            }`}
            onClick={() => setBidType('bid')}
          >
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-2">
                <ArrowUpCircle className="text-green-500" size={24} />
                <span className="font-semibold">Bid YES</span>
              </div>
              <span className="text-xs text-gray-500">Current Chance</span>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600">
                ${prediction.yesPrice.toFixed(3)}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                {(prediction.yesPrice * 100).toFixed(1)}% probability
              </p>
            </div>
          </div>

          <div 
            className={`p-6 rounded-lg border cursor-pointer transition-all ${
              bidType === 'bidAgainst' 
                ? 'border-red-500 bg-red-50' 
                : 'border-gray-200 hover:border-red-200'
            }`}
            onClick={() => setBidType('bidAgainst')}
          >
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-2">
                <ArrowDownCircle className="text-red-500" size={24} />
                <span className="font-semibold">Bid NO</span>
              </div>
              <span className="text-xs text-gray-500">Current Chance</span>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-red-600">
                ${prediction.noPrice.toFixed(3)}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                {(prediction.noPrice * 100).toFixed(1)}% probability
              </p>
            </div>
          </div>
        </div>

        {/* Market Info */}
        <div className="grid grid-cols-2 gap-4 mb-6 p-4 bg-gray-50 rounded-lg">
          <div>
            <p className="text-sm text-gray-600">24h Volume</p>
            <p className="font-medium">{formatUsdc(prediction.volume)}</p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Liquidity</p>
            <p className="font-medium">{formatUsdc(prediction.liquidity)}</p>
          </div>
        </div>

        {/* Bidding Interface */}
        {bidType && (
          <div className="border rounded-lg p-6">
            <h3 className="font-semibold mb-4">
              Place {bidType === 'bid' ? 'YES' : 'NO'} Bid
            </h3>

            <div className="space-y-4">
              <div>
                <label className="text-sm text-gray-600">Amount (USDC)</label>
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="w-full border rounded-lg p-3 mt-1"
                  placeholder="Enter amount in USDC"
                />
              </div>

              {amount && (
                <div className="bg-gray-50 p-4 rounded-lg space-y-2">
                  <div className="flex justify-between text-sm">
                    <span>Cost per Share:</span>
                    <span>${bidType === 'bid' ? prediction.yesPrice : prediction.noPrice}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span>Shares to Receive:</span>
                    <span>{calculatePayout(
                      Number(amount),
                      bidType === 'bid' ? prediction.yesPrice : prediction.noPrice
                    ).toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between font-medium pt-2 border-t">
                    <span>Max Potential Profit:</span>
                    <span className="text-green-600">{formatUsdc(
                      calculatePayout(
                        Number(amount),
                        bidType === 'bid' ? prediction.yesPrice : prediction.noPrice
                      ) - Number(amount)
                    )}</span>
                  </div>
                </div>
              )}

              <button
                className={`w-full py-3 rounded-lg font-medium text-white transition-colors ${
                  bidType === 'bid'
                    ? 'bg-green-500 hover:bg-green-600'
                    : 'bg-red-500 hover:bg-red-600'
                } disabled:bg-gray-300`}
                disabled={!amount || Number(amount) <= 0}
              >
                Place {bidType === 'bid' ? 'YES' : 'NO'} Bid
              </button>
            </div>
          </div>
        )}

        {/* Analysis */}
        <div className="mt-6">
          <h3 className="font-semibold mb-2">Analysis</h3>
          <p className="text-gray-700">{prediction.reasoning}</p>
        </div>
      </CardContent>
    </Card>
  );
};

export default PredictionCard;
