import React, { useState } from 'react';
import { Prediction } from '../types';
import { predictionApi } from '../services/api';

interface Props {
  prediction: Prediction;
  isAI: boolean;
}

const PredictionCard: React.FC<Props> = ({ prediction, isAI }) => {
  const [amount, setAmount] = useState("");
  const [isSupporting, setIsSupporting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSupport = async () => {
    if (!amount || isNaN(Number(amount))) {
      setError("Please enter a valid amount");
      return;
    }

    setIsSupporting(true);
    setError(null);

    try {
      await predictionApi.supportPrediction(
        prediction.id, 
        Number(amount), 
        isAI
      );
      setAmount("");
      // 可以添加一个成功提示
      alert("Successfully supported the prediction!");
    } catch (err) {
      console.error("Error supporting prediction:", err);
      setError("Failed to support prediction");
    } finally {
      setIsSupporting(false);
    }
  };

  return (
    <div className="border rounded-lg p-4 bg-white shadow mb-4">
      <div className="flex justify-between items-center mb-2">
        <h3 className="text-lg font-semibold">{prediction.asset}/USD</h3>
        <span className="text-sm text-gray-500">
          {isAI ? 'AI Prediction' : 'KOL Prediction'}
        </span>
      </div>

      <p className="text-2xl font-bold mb-2">
        ${prediction.predictedPrice.toLocaleString()}
      </p>

      <div className="mb-2">
        <div className="text-sm text-gray-600">Confidence: {prediction.confidence * 100}%</div>
        <div className="w-full bg-gray-200 h-2 rounded-full">
          <div 
            className="bg-blue-600 h-2 rounded-full"
            style={{ width: `${prediction.confidence * 100}%` }}
          />
        </div>
      </div>

      <p className="text-sm text-gray-600 mb-4">{prediction.reasoning}</p>

      <div className="mt-4">
        <div className="flex gap-2 mb-2">
          <input
            type="number"
            className="flex-1 border rounded px-3 py-2"
            placeholder="Enter amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button
            onClick={handleSupport}
            disabled={isSupporting}
            className={`px-4 py-2 rounded text-white ${
              isSupporting 
                ? 'bg-gray-400' 
                : 'bg-blue-500 hover:bg-blue-600'
            }`}
          >
            {isSupporting ? 'Supporting...' : 'Support'}
          </button>
        </div>
        {error && <p className="text-red-500 text-sm">{error}</p>}
      </div>
    </div>
  );
};

export default PredictionCard;
